SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_parecer_pend_usuario (nr_seq_spa_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_parecer_w		varchar(1);
nr_sequencia_w		spa_parecer.nr_sequencia%type;
qt_reg_w		bigint;


BEGIN

select	coalesce(max(a.nr_sequencia),0)
into STRICT	nr_sequencia_w
from	spa_parecer a
where	a.nr_seq_spa = nr_seq_spa_p;

select	count(*)
into STRICT	qt_reg_w
from	spa_parecer
where	nr_sequencia = nr_sequencia_w
and 	NM_USUARIO_PARECER = nm_usuario_p
and 	coalesce(DT_RESP_PARECER::text, '') = '';

if (qt_reg_w > 0) then
	ie_parecer_w := 'S';
else
	ie_parecer_w := 'N';
end if;


return	ie_parecer_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
