CREATE OR REPLACE FUNCTION obter_se_partic_anest ( nr_seq_procedimento_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE

ie_retorno_w            varchar(15) := 'N';


BEGIN
select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT    ie_retorno_w
from    procedimento_participante a
where   a.nr_sequencia = nr_seq_procedimento_p
and     a.cd_pessoa_fisica = cd_medico_p
and     a.ie_funcao = 5
and     exists (SELECT  1
                from    funcao_medico x
                where   x.cd_funcao = a.ie_funcao
                and     x.ie_anestesista = 'S');

return  ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
