CREATE OR REPLACE FUNCTION obter_se_pasta_item_perfil ( nr_seq_pasta_p bigint, nr_seq_item_perfil_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(1)	:= 'N';

BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_retorno_w
from	PERFIL_ITEM_PRONT_PASTA a,
	PRONTUARIO_ITEM_PASTA b
where	NR_SEQ_ITEM_PERFIL = nr_seq_item_perfil_p
and	b.nr_sequencia	= a.nr_seq_item_pasta
and	b.nr_seq_pasta	= nr_seq_pasta_p;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
