CREATE OR REPLACE FUNCTION obter_se_pa_internado ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_internado_w	varchar(1) := 'N';


BEGIN

select	coalesce(max('S'), 'N')
into STRICT	ie_internado_w
from	atendimento_alta b, parametro_medico p
where	nr_atendimento = nr_atendimento_p
and		ie_desfecho = 'I'
and     p.cd_estabelecimento = obter_estabelecimento_ativo
and     ((b.ie_tipo_orientacao <> 'P')
or (coalesce(p.ie_liberar_desfecho,'N')  = 'N')
or      ((b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') and (coalesce(b.dt_inativacao::text, '') = '')));

return	ie_internado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
