CREATE OR REPLACE FUNCTION obter_se_pendencia_conta (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pendencia_w	varchar(1):= 'N';
qt_pendencia_w	bigint;


BEGIN

if (coalesce(nr_interno_conta_p,0) > 0) then

	select 	count(*)
	into STRICT	qt_pendencia_w
	from 	cta_pendencia
	where 	nr_interno_conta = nr_interno_conta_p;

	if (qt_pendencia_w > 0) then

		ie_pendencia_w:= 'S';

	end if;

end if;

return	ie_pendencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
