CREATE OR REPLACE FUNCTION obter_se_pendencia_prescr ( nr_sequencia_p bigint, ie_funcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';


BEGIN

if (ie_funcao_p = 'PEP') then
select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from	pep_item_pendente
where	nr_sequencia = nr_sequencia_p
and		(((coalesce(nr_prescricao::text, '') = '') or (nr_prescricao IS NOT NULL AND nr_prescricao::text <> '')) and
		 ((coalesce(nr_seq_material_rep::text, '') = '') and (coalesce(nr_seq_proced_rep::text, '') = '') and (coalesce(nr_seq_dieta_rep::text, '') = '') and (coalesce(nr_seq_nut_pac::text, '') = '') and (coalesce(nr_seq_nut_prot::text, '') = '') and (coalesce(nr_seq_recomendacao::text, '') = '') and (coalesce(nr_seq_solucao::text, '') = '') and (coalesce(nr_seq_gasoterapia::text, '') = '') and (coalesce(nr_seq_horario::text, '') = '') and (coalesce(nr_seq_item_adep::text, '') = '') and (coalesce(nr_etapa_adep::text, '') = '') and (coalesce(ie_tipo_item_adep::text, '') = '')));
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
