CREATE OR REPLACE FUNCTION obter_se_pendencia_protocolo (NR_SEQUENCIA_P bigint) RETURNS varchar AS $body$
DECLARE


  return_w varchar(1);

BEGIN
  SELECT CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
    INTO STRICT return_w
    FROM gqa_pendencia_regra t
   WHERE t.ie_evento = '9'
     AND t.ie_situacao = 'A'
     AND t.nr_seq_pendencia = NR_SEQUENCIA_P;

  RETURN return_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
