CREATE OR REPLACE FUNCTION obter_se_pend_adep_assi ( nr_atendimento_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(1);

BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	pendencia_assinatura_adep_v
	where	nr_atendimento	= nr_atendimento_p
	and		nm_usuario  	= nm_usuario_p
	and		cd_perfil		= cd_perfil_p
	and		ie_tipo_item in ('D','S','SNE','SOL','M','MAT','P','CCG','O','HM','NPA','NAN','NPP','NPN','R','EP','PAR','GP','AN','SV','SAE','E')
	and		coalesce(dt_assinatura::text, '') = ''  LIMIT 1;
else
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	pendencia_assinatura_adep_v
	where	nm_usuario  	= nm_usuario_p
	and		cd_perfil		= cd_perfil_p
	and		ie_tipo_item in ('D','S','SNE','SOL','M','MAT','P','CCG','O','HM','NPA','NAN','NPP','NPN','R','EP','PAR','GP','AN','SV','SAE','E')
	and		coalesce(dt_assinatura::text, '') = ''  LIMIT 1;
end if;

return coalesce(ds_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
