SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_pend_exame_urgente ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
qt_exames_w			bigint;
qt_exames_pend_w	bigint;
dt_referencia_w		timestamp;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	dt_referencia_w 	:= clock_timestamp() - interval '2 days';

	select  count(1)
	into STRICT	qt_exames_w
	from 	prescr_procedimento b,
			prescr_medica a
	where	a.nr_prescricao		= b.nr_prescricao
	and		coalesce(b.nr_seq_origem::text, '') = ''
	and		a.nr_atendimento	= nr_atendimento_p
	and		a.dt_prescricao		between dt_referencia_w and (clock_timestamp() + interval '2 days')
	and		(coalesce(a.dt_liberacao, a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao, a.dt_liberacao_medico))::text <> '')
	and		b.ie_suspenso		= 'N'
	and		coalesce(b.ie_urgencia,'N') = 'S';

	if (qt_exames_w > 0) then

		select  coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from 	prescr_procedimento b,
				prescr_medica a
		where	a.nr_prescricao		= b.nr_prescricao
		and		coalesce(b.nr_seq_origem::text, '') = ''
		and		a.nr_atendimento	= nr_atendimento_p
		and		a.dt_prescricao		between dt_referencia_w and (clock_timestamp() + interval '2 days')
		and		(coalesce(a.dt_liberacao, a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao, a.dt_liberacao_medico))::text <> '')
		and		b.ie_suspenso		= 'N'
		and		coalesce(b.ie_urgencia,'N') = 'S'
		and		not exists (SELECT 1
							from  LAUDO_PACIENTE d
							where 	a.nr_atendimento = d.nr_atendimento
							and 	d.NR_PRESCRICAO = a.NR_PRESCRICAO
							and	  	b.nr_sequencia = d.nr_seq_prescricao)
		and		not exists (select 1
						from  result_laboratorio e,
											prescr_medica y
									  where y.nr_atendimento = nr_atendimento_p
									  and   y.nr_prescricao = e.nr_prescricao
									  and   e.nr_prescricao = b.nr_prescricao
									  and    e.nr_seq_prescricao = b.nr_sequencia);

	end if;

end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
