SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_perda_ganho (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE



qt_diferenca_w			double precision;
ds_retorno_w			varchar(50);
cd_estabelecimento_w		smallint;
ie_liberar_ganho_perda_w	varchar(1);


BEGIN

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	cirurgia
where	nr_cirurgia	= nr_cirurgia_p;

select	coalesce(max(ie_liberar_ganho_perda),'N')
into STRICT	ie_liberar_ganho_perda_w
from	parametro_medico
where	cd_estabelecimento	= cd_estabelecimento_w;

select	sum(CASE WHEN a.ie_perda_ganho='G' THEN  c.qt_volume  ELSE 0 END  - CASE WHEN a.ie_perda_ganho='P' THEN  c.qt_volume  ELSE 0 END ) qt_diferenca
into STRICT	qt_diferenca_w
from	tipo_perda_ganho b,
	grupo_perda_ganho a,
	atendimento_perda_ganho c
where	c.nr_cirurgia = nr_cirurgia_p
and	b.nr_sequencia = c.nr_seq_tipo
and	coalesce(b.ie_soma_bh,'S')	= 'S'
and	b.nr_seq_grupo = a.nr_sequencia
and	((ie_liberar_ganho_perda_w = 'N') or (c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> ''))
and     coalesce(c.ie_situacao,'A') = 'A';

if (qt_diferenca_w > 0 ) then
	ds_retorno_w := wheb_mensagem_pck.get_texto(309372); -- Positivo
elsif (qt_diferenca_w < 0 ) then
	ds_retorno_w := wheb_mensagem_pck.get_texto(309373); -- Negativo
elsif (qt_diferenca_w = 0 ) then
	ds_retorno_w := wheb_mensagem_pck.get_texto(309374); -- Neutro
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_perda_ganho (nr_cirurgia_p bigint) FROM PUBLIC;

