CREATE OR REPLACE FUNCTION obter_se_perfil_equip (cd_equipamento_p bigint, cd_perfil_p bigint, cd_convenio_p text default null) RETURNS varchar AS $body$
DECLARE


qt_registro_w	integer;
ie_retorno_w	varchar(1);


BEGIN

if (coalesce(cd_equipamento_p,0) > 0) and (coalesce(cd_perfil_p,0) > 0) then
	select 	count(*)
	into STRICT	qt_registro_w
	from	liberacao_equipamento
	where	cd_equipamento	=	cd_equipamento_p;

	if (qt_registro_w > 0 ) then

		select 	coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from	liberacao_equipamento
		where	cd_equipamento	=	cd_equipamento_p
		and		coalesce(cd_perfil,coalesce(cd_perfil_p,0))		= coalesce(cd_perfil_p,0)
		and		coalesce(cd_convenio,coalesce(cd_convenio_p,0))	= coalesce(cd_convenio_p,0)
		and 	not exists (SELECT 1
							from	liberacao_equipamento
							where	cd_equipamento	=	cd_equipamento_p
							and		coalesce(cd_perfil,coalesce(cd_perfil_p,0))		= coalesce(cd_perfil_p,0)
							and		coalesce(cd_convenio,coalesce(cd_convenio_p,0))	= coalesce(cd_convenio_p,0)
							and 	ie_exclusao = 'S')
		and 	ie_exclusao <> 'S';

	else
		ie_retorno_w := 'S';
	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
