CREATE OR REPLACE FUNCTION obter_se_perfil_exame ( nr_seq_exame_p bigint, cd_perfil_ativo_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_utiliza_w	varchar(1) := 'N';


BEGIN

select	coalesce(max('N'),'S')
into STRICT	ie_utiliza_w
from	med_exame_padrao_perfil
where	nr_seq_exame_padrao	=	nr_seq_exame_p;

if (ie_utiliza_w = 'N') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_utiliza_w
	from	med_exame_padrao_perfil
	where	nr_seq_exame_padrao	=	nr_seq_exame_p
	and	cd_perfil		=	cd_perfil_ativo_p;
end if;

return	ie_utiliza_w;

end;
$body$
