CREATE OR REPLACE FUNCTION obter_se_perfil_liberado ( cd_perfil_p bigint, cd_funcao_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_perfil_liberado_w	varchar(1);


BEGIN

select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_perfil_liberado_w
from	usuario_perfil a,
	funcao_perfil b
where	a.cd_perfil 		= b.cd_perfil
and	a.cd_perfil 		= cd_perfil_p
and	upper(a.nm_usuario) 	= upper(nm_usuario_p)
and	b.cd_funcao 		= cd_funcao_p;

return	ie_perfil_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
