CREATE OR REPLACE FUNCTION obter_se_perfil_lib ( cd_perfil_p bigint, cd_funcao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_perfil_liberado_w	varchar(1);


BEGIN

select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_perfil_liberado_w
from 	funcao_perfil
where 	cd_perfil = cd_perfil_p
and	  	cd_funcao = cd_funcao_p;

return	ie_perfil_liberado_w;

end;
$body$
