CREATE OR REPLACE FUNCTION obter_se_perfil_nut_liberado ( nr_seq_perfil_pf_p bigint, nr_seq_nut_reserva_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(1);
qt_perfis_w	smallint;


BEGIN

select	count(*)
into STRICT	qt_perfis_w
from	nut_perfil_reserva
where	nr_seq_nut_reserva 	= nr_seq_nut_reserva_p;

if (qt_perfis_w = 0) then
	ds_retorno_w := 'S';
else
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	nut_perfil_reserva
	where	nr_seq_perfil 		= nr_seq_perfil_pf_p
	and	nr_seq_nut_reserva 	= nr_seq_nut_reserva_p;
end if;

return	ds_retorno_w;

end;
