CREATE OR REPLACE FUNCTION obter_se_perfil_usuario ( cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_perfil_usuario_w	varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_perfil_usuario_w
from	usuario_perfil
where	cd_perfil = cd_perfil_p
and	upper(nm_usuario) = upper(nm_usuario_p);

return	ie_perfil_usuario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
