SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_periodo_retencao (cd_pessoa_fisica_p text, nr_seq_modulo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_meses_retencao_w	priv_regra_retencao.qt_meses_retencao%type;
dt_referencia_w		timestamp;


BEGIN

select	max(qt_meses_retencao)
into STRICT	qt_meses_retencao_w
from	priv_regra_retencao
where	coalesce(nr_seq_modulo,114) = coalesce(nr_seq_modulo_p,114);

select	max(dt_alta)
into STRICT	dt_referencia_w
from	atendimento_paciente
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (coalesce(dt_referencia_w::text, '') = '') then
	select	max(dt_atualizacao)
	into STRICT	dt_referencia_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
end if;


if ((pkg_date_utils.add_month(dt_referencia_w, qt_meses_retencao_w, 0)) <= clock_timestamp()) then
	ds_retorno_w := 'N';
else
	ds_retorno_w := 'S';
end if;


RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
