CREATE OR REPLACE FUNCTION obter_se_permite_acessorio ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_seq_proc_interno_p proc_interno.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_tipo_acessorio_w	varchar(1);

ds_retorno_w	varchar(1) := 'N';


BEGIN

if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then
	begin
		select	(CASE WHEN (max(ap.ie_tipo_acessorio) IS NOT NULL AND (max(ap.ie_tipo_acessorio))::text <> '') THEN 'S' ELSE 'N' END)
		into STRICT	ie_tipo_acessorio_w
		from	acessorio_paciente ap,
				paciente_acessorio pa
		where	ap.nr_sequencia = pa.nr_seq_acessorio
		and		(ap.ie_tipo_acessorio IS NOT NULL AND ap.ie_tipo_acessorio::text <> '')
		and		ap.ie_tipo_acessorio = 'MAR'
		and		pa.cd_pessoa_fisica = cd_pessoa_fisica_p
		and		(pa.dt_liberacao IS NOT NULL AND pa.dt_liberacao::text <> '')
		and 	coalesce(pa.dt_inativacao::text, '') = '';
		
		if (ie_tipo_acessorio_w = 'S') then
			begin
				select	coalesce(max(ie_controla_marcapasso), 'N')
				into STRICT	ds_retorno_w
				from	proc_interno
				where	nr_sequencia = nr_seq_proc_interno_p
				and		coalesce(ie_controla_marcapasso,'N') = 'S';
			end;
		end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
