CREATE OR REPLACE FUNCTION obter_se_permite_agendar_prior (nr_seq_agenda_lista_espera_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);				
nr_seq_regra_w			bigint;
ds_retorno_w			varchar(2) := 'N';


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ds_retorno_w
from	agenda_lista_espera a,
		regulacao_Atend b
where	a.nr_seq_regulacao = b.nr_sequencia
and		UPPER(b.ie_status) not in ('CA', 'NG')
and		a.nr_sequencia = nr_seq_agenda_lista_espera_p
and		coalesce(a.ie_consorcio,'N') <> 'S';

If (ds_retorno_w = 'S') then

	ds_retorno_w := 'N';

	select	max(a.cd_pessoa_fisica)
	into STRICT 	cd_pessoa_fisica_w
	from	usuario a
	where	a.nm_usuario = nm_usuario_p;

	select	max(nr_sequencia)
	into STRICT 	nr_seq_regra_w
	from	regra_lib_lista_espera
	where 	cd_pessoa_fisica = cd_pessoa_fisica_w
	and 	ie_situacao = 'A';

	
	if ( coalesce( nr_seq_regra_w,0) > 0)then
		ds_retorno_w := 'S';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
