CREATE OR REPLACE FUNCTION obter_se_permite_agora ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_grupo_material_w		integer;
cd_subgrupo_material_w	integer;
cd_classe_material_w	integer;
ie_permite_agora_w		varchar(10);

c01 CURSOR FOR
SELECT	ie_permite_agora
from	rep_restricao_medicamento
where	coalesce(cd_grupo_material,coalesce(cd_grupo_material_w,0))		= coalesce(cd_grupo_material_w,0)
and		coalesce(cd_subgrupo_material,coalesce(cd_subgrupo_material_w,0))	= coalesce(cd_subgrupo_material_w,0)
and 	coalesce(cd_classe_material,coalesce(cd_classe_material_w,0))		= coalesce(cd_classe_material_w,0)
and		coalesce(cd_material,coalesce(cd_material_p,0))					= coalesce(cd_material_p,0)
order by
	coalesce(cd_grupo_material,0),
	coalesce(cd_subgrupo_material,0),
	coalesce(cd_classe_material,0),
	coalesce(cd_material,0);


BEGIN

select 	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
from 	estrutura_material_v
where	cd_material	= cd_material_p;

open C01;
loop
fetch C01 into
	ie_permite_agora_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ie_permite_agora_w	:= ie_permite_agora_w;
end loop;
close C01;


return	ie_permite_agora_w;

end;
$body$
