CREATE OR REPLACE FUNCTION obter_se_permite_alt_prot ( nr_seq_paciente_p bigint, cd_material_p bigint, ie_pasta_p text DEFAULT 'X') RETURNS varchar AS $body$
DECLARE



ie_alterar_w		varchar(1) := 'S';

cd_grupo_material_w	smallint;
cd_subgrupo_w		smallint;
cd_classe_material_w	integer;

ds_retorno_w		varchar(2) := 'S';
cd_protocolo_w		varchar(10);
nr_seq_medicacao_w	bigint;
cd_material_w		varchar(10);

C01 CURSOR FOR
	SELECT			coalesce(ie_permite_alterar,'S')
	FROM	regra_alt_prot_prescricao
	WHERE	coalesce(cd_protocolo,cd_protocolo_w)	= cd_protocolo_w
	AND	coalesce(nr_seq_medicacao,nr_seq_medicacao_w)= nr_seq_medicacao_w
	AND	coalesce(cd_material,cd_material_p) 		= cd_material_p
	AND	coalesce(cd_grupo_mat, cd_grupo_material_w)	= cd_grupo_material_w
	AND	coalesce(cd_subgrupo_mat, cd_subgrupo_w)	= cd_subgrupo_w
	AND	coalesce(cd_classe_mat, cd_classe_material_w)= cd_classe_material_w
	AND  	coalesce(ie_pasta,ie_pasta_p)  		= ie_pasta_p
	AND	  (cd_protocolo IS NOT NULL AND cd_protocolo::text <> '')
	ORDER BY coalesce(cd_protocolo,0),
		 coalesce(nr_seq_medicacao,0),
		 coalesce(cd_material,0);

	c02 CURSOR FOR
	SELECT	coalesce(ie_permite_alterar,'S')
	FROM regra_alt_prot_prescricao
	WHERE coalesce(cd_protocolo, cd_protocolo_w) = cd_protocolo_w
	AND   coalesce(nr_seq_medicacao, nr_seq_medicacao_w) = nr_seq_medicacao_w
	AND   coalesce(ie_pasta, ie_pasta_p) =  ie_pasta_p
	AND	  (cd_protocolo IS NOT NULL AND cd_protocolo::text <> '');

BEGIN

IF (nr_seq_paciente_p > 0) AND (cd_material_p > 0 ) THEN

SELECT  MAX(cd_protocolo),
		MAX(nr_seq_medicacao)
INTO STRICT	cd_protocolo_w,
		nr_seq_medicacao_w
FROM	paciente_setor a
WHERE	nr_seq_paciente = nr_seq_paciente_p;



	SELECT	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	INTO STRICT	cd_grupo_material_w,
		cd_subgrupo_w,
		cd_classe_material_w
	FROM	estrutura_material_v
	WHERE	cd_material	= cd_material_p;


	IF (cd_protocolo_w > 0 ) AND (cd_material_p > 0 ) THEN

		OPEN C01;
		LOOP
		FETCH C01 INTO
			ie_alterar_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */

			ds_retorno_w :=  ie_alterar_w;

		END LOOP;
		CLOSE C01;
	ELSIF (cd_protocolo_w > 0) THEN
		OPEN c02;
		LOOP
		FETCH c02 INTO
			ie_alterar_w;

			EXIT WHEN NOT FOUND; /* apply on c02 */

			 ds_retorno_w := ie_alterar_w;
		END LOOP;
		CLOSE c02;
	END IF;

END IF;

RETURN  ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
