CREATE OR REPLACE FUNCTION obter_se_permite_area_contrato ( nr_seq_area_p bigint, cd_perfil_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w			regra_liberacao_contrato.nr_sequencia%type;
nr_seq_area_regra_w		regra_liberacao_contrato.nr_seq_area%type;
ie_permite_acesso_w		regra_liberacao_contrato.ie_permite_acesso%type;
ie_permite_acesso_regra_w	regra_liberacao_contrato.ie_permite_acesso%type;
qt_registros_w			bigint;

c01 CURSOR FOR
SELECT	nr_sequencia,
	nr_seq_area,
	ie_permite_acesso
from	regra_liberacao_contrato
where	ie_situacao = 'A'
and	((coalesce(nr_seq_area,0) = 0) or (coalesce(nr_seq_area,0) > 0) and (coalesce(nr_seq_area,0) = coalesce(nr_seq_area_p,0)))
order by	coalesce(nr_seq_area,99999);


BEGIN

ie_permite_acesso_w := 'S';

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_area_regra_w,
	ie_permite_acesso_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_registros_w
	from	regra_lib_contrato_setor
	where	nr_seq_regra_lib = nr_sequencia_w;

	if (qt_registros_w = 0) and (ie_permite_acesso_regra_w = 'N') then
		ie_permite_acesso_w := 'N';
	else


		select	count(*)
		into STRICT	qt_registros_w
		from	regra_lib_contrato_setor
		where	nr_seq_regra_lib = nr_sequencia_w
		and	coalesce(cd_setor_atendimento,cd_setor_atendimento_p) = cd_setor_atendimento_p
		and	coalesce(cd_perfil,cd_perfil_p) = cd_perfil_p
		and	coalesce(nm_usuario_lib,nm_usuario_p) = nm_usuario_p;

		if	((qt_registros_w = 0) or
			(qt_registros_w > 0 AND ie_permite_acesso_regra_w = 'N')) then
			ie_permite_acesso_w := 'N';
		end if;
	end if;
exit;
end;
end loop;
close C01;

return ie_permite_acesso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
