CREATE OR REPLACE FUNCTION obter_se_permite_cad_material ( ie_tipo_material_p bigint, nr_seq_grupo_rec_p bigint, cd_classe_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';

c01 CURSOR FOR
SELECT	1
from	regra_cadastro_material a,
	regra_cadastro_mat_estrut b
where	a.nr_sequencia = b.nr_seq_regra
and	coalesce(ie_tipo_material, ie_tipo_material_p) = ie_tipo_material_p
and	coalesce(cd_classe_material, cd_classe_material_p) = cd_classe_material_p
and	((coalesce(nr_seq_grupo_rec, nr_seq_grupo_rec_p)	= nr_seq_grupo_rec_p) or (coalesce(nr_seq_grupo_rec_p::text, '') = ''))
order by
	coalesce(ie_tipo_material,0),
	coalesce(cd_classe_material,0),
	coalesce(nr_seq_grupo_rec,0);


BEGIN

open C01;
loop
fetch C01 into
	ds_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w := 'S';
	end;
end loop;
close C01;


return	ds_retorno_w;

end;
$body$
