CREATE OR REPLACE FUNCTION obter_se_permite_calc_cotacao ( nr_cot_compra_p bigint) RETURNS varchar AS $body$
DECLARE



/*Essa function é utilizada especificamente para o parâmetro da cotação de compras
Vai permitir calcular caso a data de retorno seje menor que a data atual, ou caso não tenha nenhum fornecedor na cotação com status
	AH              Aberta no hospital
	AF              Em andamento com o fornecedor
	LH              Liberada pelo hospital para o fornecedor
	LF              Lida pelo Fornecedor*/
ie_permite_calcular_w			varchar(1) := 'N';
dt_retorno_prev_w				timestamp;
qt_registro_w				bigint;


BEGIN

select	dt_retorno_prev
into STRICT	dt_retorno_prev_w
from	cot_compra
where	nr_cot_compra = nr_cot_compra_p;

if (dt_retorno_prev_w < clock_timestamp()) then
	ie_permite_calcular_w := 'S';
else
	select	count(*)
	into STRICT	qt_registro_w
	from	cot_compra_forn
	where	nr_cot_compra = nr_cot_compra_p;

	if (qt_registro_w > 0) then
		select	count(*)
		into STRICT	qt_registro_w
		from	cot_compra_forn
		where	nr_cot_compra = nr_cot_compra_p
		and	((coalesce(ie_status::text, '') = '') or (ie_status in ('AH','AF','LH','LF')));

		if (qt_registro_w = 0) then
			ie_permite_calcular_w := 'S';
		end if;
	end if;
end if;

return	ie_permite_calcular_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
