CREATE OR REPLACE FUNCTION obter_se_permite_cfinanc_nf ( cd_conta_financ_p bigint, nr_seq_nf_p bigint, nr_item_nf_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(1)	:= 'S';
ie_oper_fluxo_w			varchar(1);
cd_estabelecimento_w		bigint;

c01 CURSOR FOR
SELECT	coalesce(ie_permite,'S')
from	consiste_conta_financ_nf
where	cd_estabelecimento = cd_estabelecimento_w
and	ie_situacao = 'A'
and	ie_oper_fluxo = ie_oper_fluxo_w;


BEGIN

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	nota_fiscal
where	nr_sequencia = nr_seq_nf_p;

select	ie_oper_fluxo
into STRICT	ie_oper_fluxo_w
from	conta_financeira
where	cd_conta_financ = cd_conta_financ_p;

open C01;
loop
fetch C01 into
	ie_permite_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_permite_w := ie_permite_w;
	end;
end loop;
close C01;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
