CREATE OR REPLACE FUNCTION obter_se_permite_codificacao (CD_DOENCA_CID_P text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2);


BEGIN

if ((CD_DOENCA_CID_P IS NOT NULL AND CD_DOENCA_CID_P::text <> '') and (coalesce(pkg_i18n.get_user_locale, 'pt_BR') in ('de_AT'))) then
	
  select (CASE WHEN (max(DS_DOENCA_CID) IS NOT NULL AND (max(DS_DOENCA_CID))::text <> '') THEN 'N' ELSE 'S' END)
    into STRICT ds_retorno_w
	from CID_DOENCA
	where cd_doenca_cid = upper( CD_DOENCA_CID_P )
	and (ie_codificacao = 'N')
	order by 1;

else

	ds_retorno_w := 'S';

end if;

return ds_retorno_w;

END;
$body$
