CREATE OR REPLACE FUNCTION obter_se_permite_diluente ( cd_material_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);


BEGIN

if (coalesce(cd_material_p,0) > 0) then
	select 	coalesce(max(ie_permite),'S')
	into STRICT	ds_retorno_w
	from	regra_medic_nao_diluicao
	where	((coalesce(cd_material::text, '') = '') or (cd_material = cd_material_p))
	and	((coalesce(cd_grupo_material::text, '') = '') or (cd_grupo_material = Obter_estrutura_material(cd_material_p,'G')))
	and	((coalesce(cd_subgrupo_material::text, '') = '') or (cd_subgrupo_material = Obter_estrutura_material(cd_material_p,'S')))
	and	((coalesce(nr_seq_familia::text, '') = '') or (nr_seq_familia = obter_familia_material(cd_material_p)))
	and	((coalesce(cd_classe_material::text, '') = '') or (cd_classe_material = Obter_estrutura_material(cd_material_p,'C')));
end if;

return coalesce(ds_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
