CREATE OR REPLACE FUNCTION obter_se_permite_gerar_desc ( cd_convenio_parametro_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

			
ie_gerar_desconto_w			varchar(10);
ie_varpermitegerardesconto_w		varchar(10);
ie_retorno_w				varchar(10)	:= 'N';


BEGIN

select	coalesce(max(b.ie_gerar_desconto),'N')
into STRICT	ie_gerar_desconto_w
from	convenio_estabelecimento b
where	b.cd_convenio		= cd_convenio_parametro_p
and	b.cd_estabelecimento	= cd_estabelecimento_p;

ie_varpermitegerardesconto_w	:= coalesce(obter_valor_param_usuario(67, 58, obter_perfil_ativo, coalesce(nm_usuario_p, obter_usuario_ativo), 0), 'S');

if (ie_gerar_desconto_w	= 'S') or (ie_varpermitegerardesconto_w = 'S') then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
