CREATE OR REPLACE FUNCTION obter_se_permite_internacao (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
nr_retorno_proc_w	bigint;
nr_retorno_material_w	bigint;
nr_prescricao_w		bigint;
var_entrou_w		boolean := false;

c01 CURSOR FOR
SELECT	a.nr_prescricao
from	prescr_medica a
where	a.nr_atendimento = nr_atendimento_p
and	((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> ''));


BEGIN
open 	c01;
	loop
	fetch 	c01 into
		nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		if (ds_retorno_w <> 'N') then
			begin

			select count(*)
			into STRICT	nr_retorno_proc_w
			from	prescr_medica x
			where	x.nr_prescricao =	nr_prescricao_w
			and	not exists (SELECT 1
						from procedimento_paciente c
						where c.nr_atendimento = nr_atendimento_p
						and   c.nr_prescricao = x.nr_prescricao);
			select count(*)
			into STRICT	nr_retorno_material_w
			from	prescr_medica x
			where	x.nr_prescricao =	nr_prescricao_w
			and	not exists (SELECT 1
						from material_atend_paciente c
						where c.nr_atendimento = nr_atendimento_p
						and   c.nr_prescricao = x.nr_prescricao);
			end;

		if (nr_retorno_proc_w > 0 )
		and (nr_retorno_material_w > 0) then
			ds_retorno_w := 'N';
		end if;

		end if;
	end;
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
