CREATE OR REPLACE FUNCTION obter_se_permite_internar ( nr_seq_tipo_episodio_p subtipo_episodio.nr_seq_tipo_episodio%type, nr_seq_tipo_admissao_p subtipo_episodio.nr_seq_tipo_admissao%type, dt_internacao_p timestamp, dt_entrada_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
ie_permite_internar_w	SUBTIPO_EPISODIO.ie_permite_internar%type;


BEGIN

ds_retorno_w := 'S';

if ((nr_seq_tipo_episodio_p IS NOT NULL AND nr_seq_tipo_episodio_p::text <> '') and
	(nr_seq_tipo_admissao_p IS NOT NULL AND nr_seq_tipo_admissao_p::text <> '') and
		(dt_internacao_p IS NOT NULL AND dt_internacao_p::text <> '') and 
			(dt_entrada_p IS NOT NULL AND dt_entrada_p::text <> ''))then
	
	select 	max(ie_permite_internar)
	into STRICT	ie_permite_internar_w
	from 	subtipo_episodio
	where 	nr_seq_tipo_episodio = nr_seq_tipo_episodio_p
	and 	nr_seq_tipo_admissao = nr_seq_tipo_admissao_p;

	if (ie_permite_internar_w IS NOT NULL AND ie_permite_internar_w::text <> '') then
		if (ie_permite_internar_w = 1 and trunc(dt_internacao_p) < trunc(dt_entrada_p)) then
			ds_retorno_w := 'N';
		elsif (ie_permite_internar_w = 2 and trunc(dt_internacao_p) <= trunc(dt_entrada_p)) then
			ds_retorno_w := 'N';
		end if;
	end if;
end if;

return ds_retorno_w;

end;
