CREATE OR REPLACE FUNCTION obter_se_permite_lactante ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, cd_material_p material.cd_material%type) RETURNS varchar AS $body$
DECLARE

						 
ie_sexo_pf_w		pessoa_fisica.ie_sexo%type;
ds_retorno_w		varchar(1) := 'S';

BEGIN
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND cd_material_p IS NOT NULL AND cd_material_p::text <> '') then 
	select 	obter_sexo_pf(obter_pessoa_Atendimento(nr_atendimento_p,'C'),'C') 
	into STRICT	ie_sexo_pf_w 
	;
 
	if (ie_sexo_pf_w = 'F') then 
		select	coalesce(max('N'), 'S') 
		into STRICT	ds_retorno_w 
		from	material 
		where	cd_material = cd_material_p 
		and		coalesce(ie_permite_lactante, 'N') = 'S' --Rotina alterada. Quando este campo estiver checado ir√° consistir lactante. 
		and		coalesce(obter_inf_saude_mulher(nr_atendimento_p, 'A'), 'N') = 'S';
	end if;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
