CREATE OR REPLACE FUNCTION obter_se_permite_lancar (nr_atendimento_p bigint, cd_setor_p bigint) RETURNS varchar AS $body$
DECLARE

 
IE_FIM_CONTA_W		varchar(1);
EXISTE_PROCESSO_W		real;


BEGIN 
SELECT 	IE_FIM_CONTA 
INTO STRICT		IE_FIM_CONTA_W 
FROM		ATENDIMENTO_PACIENTE 
WHERE		NR_ATENDIMENTO = NR_ATENDIMENTO_P;
if (IE_FIM_CONTA_W = 'A')	then 
	return 'S';
else	 
	SELECT 	DISTINCT coalesce(max(1),0) 
	INTO STRICT		EXISTE_PROCESSO_W 
	FROM 		PROCESSO_ATENDIMENTO 
	WHERE 	NR_ATENDIMENTO = NR_ATENDIMENTO_P 
	AND 		CD_SETOR_RESP = CD_SETOR_P 
	AND 		coalesce(DT_FIM_REAL::text, '') = '';
	if (EXISTE_PROCESSO_W = 1)	then 
		return 'S';
	else 
		SELECT 	COUNT(*) 
		INTO STRICT		EXISTE_PROCESSO_W 
		FROM		PROCESSO_ATENDIMENTO 
		WHERE 	NR_ATENDIMENTO = NR_ATENDIMENTO_P 
		AND 		CD_SETOR_RESP = CD_SETOR_P;
		if (EXISTE_PROCESSO_W = 0)	then 
			return 'S';
		else 
			return 'N';
		end if;
	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
