SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_permite_lib_soap (nr_seq_item_pront_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
ie_nivel_atencao_w		varchar(1) := 'T';



BEGIN


Select 	coalesce(max(ie_nivel_atencao),'T')
into STRICT	ie_nivel_atencao_w
from	perfil
where 	cd_perfil = cd_perfil_p;


if (ie_nivel_atencao_w = 'P') or (ie_nivel_atencao_w = 'S') then

	Select 	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from   	prontuario_item
	where	ie_soap = 'S'
	and	nr_sequencia = nr_seq_item_pront_p;
end if;


return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
