CREATE OR REPLACE FUNCTION obter_se_permite_mov_cc ( ie_regra_p text, nr_atendimento_p bigint, cd_setor_dest_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_classif_setor_dest_w	varchar(2);
cd_classif_setor_orig_w	varchar(2);
cd_setor_origem_w	integer;
ie_liberado_w		varchar(1);


BEGIN 
 
ie_liberado_w := 'N';
 
select	cd_classif_setor 
into STRICT	cd_classif_setor_dest_w 
from	setor_atendimento 
where	cd_setor_atendimento = cd_setor_dest_p;
 
select	obter_unidade_atendimento(nr_atendimento_p,'IAA','CS') 
into STRICT	cd_setor_origem_w
;
 
select	cd_classif_setor 
into STRICT	cd_classif_setor_orig_w 
from	setor_atendimento 
where	cd_setor_atendimento = cd_setor_origem_w;
 
if (cd_classif_setor_orig_w not in (3,4,8)) and (cd_classif_setor_dest_w = 2) and (ie_regra_p = 'S') then 
	ie_liberado_w := 'N';
else	 
	ie_liberado_w := 'S';
end if;
 
return	ie_liberado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
