SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_permite_nova_evolucao ( cd_tipo_evolucao_p text, nr_atendimento_p bigint, cd_evolucao_p bigint default 0) RETURNS varchar AS $body$
DECLARE


qt_regra_w  bigint;
qt_registro_w bigint;
ds_retorno_w varchar(1);
ie_lib_evolucao_paciente_w	varchar(1);


BEGIN
ds_retorno_w := 'S';

ie_lib_evolucao_paciente_w := obter_param_usuario(0, 37, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_lib_evolucao_paciente_w);

if (cd_tipo_evolucao_p IS NOT NULL AND cd_tipo_evolucao_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p > 0) then
	begin
	select 	coalesce(qt_max_reg,0)
	into STRICT	qt_regra_w
	from	tipo_evolucao
	where 	cd_tipo_evolucao = cd_tipo_evolucao_p;


	if (qt_regra_w > 0) then
		select  count(1)
		into STRICT 	qt_registro_w
		from  evolucao_paciente
		where ie_evolucao_clinica = cd_tipo_evolucao_p
		and  	nr_atendimento = nr_atendimento_p
    and   cd_evolucao <> cd_evolucao_p
		and		((ie_lib_evolucao_paciente_w = 'N') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''))
		and 	coalesce(dt_inativacao::text, '') = '';

		if (qt_regra_w <= qt_registro_w) then
			ds_retorno_w := 'N';
		end if;

	end if;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
