SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_permite_nova_ivc (nr_seq_ivc_p bigint, nr_seq_horario_p bigint, ie_encerrar_ivc_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(1)	:= 'S';
nr_sequencia_w			bigint;
nr_seq_procedimento_w		integer;
nr_prescricao_w			bigint;
nr_atendimento_w		bigint;
ie_possui_prescr_w		varchar(1);


BEGIN
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') or (nr_seq_ivc_p IS NOT NULL AND nr_seq_ivc_p::text <> '') then

	if (coalesce(nr_seq_ivc_p,0)	<> 0) then

		select	max(nr_prescricao),
			max(nr_atendimento)
		into STRICT	nr_prescricao_w,
			nr_atendimento_w
		from	adep_irrigacao
		where	nr_sequencia	= nr_seq_ivc_p;

		nr_sequencia_w	:= nr_seq_ivc_p;

	else
		select	max(nr_prescricao),
			max(nr_seq_procedimento)
		into STRICT	nr_prescricao_w,
			nr_seq_procedimento_w
		from	prescr_proc_hor
		where	nr_sequencia	= nr_seq_horario_p;

		nr_sequencia_w	:= 0;

		select	max(nr_atendimento)
		into STRICT	nr_atendimento_w
		from	prescr_medica
		where	nr_prescricao	= nr_prescricao_w;
	end if;

	select	coalesce(max(ie_prescr_emergencia),'N')
	into STRICT	ie_permite_w
	from	prescr_medica
	where	nr_prescricao	= nr_prescricao_w;

	if (ie_permite_w	= 'N') then

		if (nr_sequencia_w	= 0) then

			select	coalesce(max(nr_sequencia),0)
			into STRICT	nr_sequencia_w
			from	adep_irrigacao
			where	nr_prescricao		= nr_prescricao_w
			and	nr_seq_procedimento	= nr_seq_procedimento_w;


		end if;

		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_permite_w
		from	adep_irrigacao a,
			prescr_medica	b
		where	b.nr_atendimento	= a.nr_atendimento
		and	b.nr_atendimento	= nr_atendimento_w
		and	a.nr_atendimento	= nr_atendimento_w
		and	a.nr_prescricao		= b.nr_prescricao
		and	a.ie_status_ivc	= 'I'
		and	a.nr_sequencia	<> nr_sequencia_w
		and	((b.dt_validade_prescr	> clock_timestamp()) or (ie_encerrar_ivc_p	= 'N'));

	end if;

end if;

return ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
