CREATE OR REPLACE FUNCTION obter_se_permite_novo_prot ( ie_tipo_protocolo_p text, cd_convenio_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w		varchar(1) := 'S';
ie_regra_w		integer;
ie_liberado_w		integer;
ie_consiste_prot_prov_w	varchar(1);
qt_prot_prov_w		bigint;
	

BEGIN 
 
select 	count(*) 
into STRICT	ie_regra_w 
from	tipo_prot_lib_conv 
where	cd_convenio = cd_convenio_p        
and	cd_estabelecimento = cd_estabelecimento_p;
 
if (ie_regra_w > 0) then 
 
	select 	count(*) 
	into STRICT	ie_liberado_w 
	from	tipo_prot_lib_conv 
	where	cd_convenio = cd_convenio_p        
	and	cd_estabelecimento = cd_estabelecimento_p 
	and	ie_tipo_protocolo = ie_tipo_protocolo_p;
 
	if (ie_liberado_w > 0) then 
		 
		select	coalesce(max(ie_consiste_prot_prov),'N') 
		into STRICT	ie_consiste_prot_prov_w 
		from	tipo_prot_lib_conv 
		where	cd_convenio = cd_convenio_p        
		and	cd_estabelecimento = cd_estabelecimento_p 
		and	ie_tipo_protocolo = ie_tipo_protocolo_p;
		 
		if (ie_consiste_prot_prov_w = 'S') then 
		 
			select	count(*) 
			into STRICT	qt_prot_prov_w 
			from	protocolo_convenio 
			where	cd_convenio = cd_convenio_p 
			and	ie_tipo_protocolo = ie_tipo_protocolo_p 
			and	cd_estabelecimento = cd_estabelecimento_p 
			and	ie_status_protocolo = 1;
			 
			if (qt_prot_prov_w > 0) then 
				ie_retorno_w	:= 'N';
			else 
				ie_retorno_w	:= 'S';
			end if;
		 
		else 
			ie_retorno_w	:= 'S';
		end if;
		 
	else 
		ie_retorno_w	:= 'S';
	end if;
else 
	ie_retorno_w	:= 'S';
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
