CREATE OR REPLACE FUNCTION obter_se_permite_nut (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(1) := 'S';
cd_classe_material_w		integer;
cd_subgrupo_material_w		smallint;
cd_grupo_material_w		smallint;
cont_w				bigint;

c01 CURSOR FOR
SELECT	ie_permite
from	rep_regra_item_nutricao a
where	coalesce(cd_material, cd_material_p) = cd_material_p
and	coalesce(cd_classe_material,cd_classe_material_w)	= cd_classe_material_w
and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w)= cd_subgrupo_material_w
and	coalesce(cd_grupo_material,cd_grupo_material_w)	= cd_grupo_material_w
and	coalesce(cd_Perfil, coalesce(obter_perfil_ativo,0))	= coalesce(obter_perfil_ativo,0)
order by a.cd_grupo_material,
	a.cd_subgrupo_material,
	a.cd_classe_material,
	a.cd_material;


BEGIN

select	count(*)
into STRICT	cont_w
from	rep_regra_item_nutricao;

if (cont_w > 0) then

	select	cd_classe_material,
		cd_subgrupo_material,
		cd_grupo_material
	into STRICT	cd_classe_material_w,
		cd_subgrupo_material_w,
		cd_grupo_material_w
	from	estrutura_material_v
	where	cd_material	= cd_material_p;

	open C01;
	loop
	fetch C01 into
		ie_permite_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ie_permite_w	:= ie_permite_w;
	end loop;
	close C01;
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
