CREATE OR REPLACE FUNCTION obter_se_permite_partir ( cd_material_p cpoe_material.cd_material%type, qt_dose_p cpoe_material.qt_dose%type, cd_unidade_medida_p cpoe_material.cd_unidade_medida%type) RETURNS varchar AS $body$
DECLARE


ie_permite_partir_w		material.ie_permite_partir%type;
qt_divisor_minimo_w		material_conversao_unidade.qt_divisor_minimo%type;
ds_just_particao_med_w	cpoe_justificativa_item.ds_just_particao_med%type;
qt_conversao_w			material_conversao_unidade.qt_conversao%type;
ds_retorno_s			varchar(2) := 'S';


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select	ie_permite_partir
	into STRICT	ie_permite_partir_w
	from 	material
	where 	cd_material = cd_material_p;

	select	coalesce(max(qt_divisor_minimo),0),
			coalesce(max(qt_conversao),0)
	into STRICT 	qt_divisor_minimo_w,
			qt_conversao_w
	from	material_conversao_unidade
	where	cd_material = cd_material_p 
	and		cd_unidade_medida = cd_unidade_medida_p;

	select 	max(ds_just_particao_med)
	into STRICT 	ds_just_particao_med_w
	from 	cpoe_justificativa_item
	where 	nr_seq_material = cd_material_p 
	and		(ds_just_particao_med IS NOT NULL AND ds_just_particao_med::text <> '');

	if (ie_permite_partir_w = 'NP') and (qt_conversao_w <> qt_dose_p) then
		ds_retorno_s := 'P';

	elsif (qt_divisor_minimo_w > 0 and
		mod(qt_dose_p, qt_divisor_minimo_w) <> 0) then

		if (ie_permite_partir_w = 'P') then 
			ds_retorno_s := 'P';	
		elsif (ie_permite_partir_w = 'PC') and (coalesce(ds_just_particao_med_w::text, '') = '') then
			ds_retorno_s := 'PC';
		end if;		

	end if;	
end if;

return ds_retorno_s;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
