CREATE OR REPLACE FUNCTION obter_se_permite_passagem (nr_atendimento_p bigint, dt_passagem_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_prim_passagem_w		timestamp;
ie_permite_passagem_inf_w	varchar(1);
ie_retorno_w			varchar(1);


BEGIN

select	min(dt_entrada_unidade)
into STRICT	dt_prim_passagem_w
from 	atend_paciente_unidade
where   nr_atendimento = nr_atendimento_p;

select	coalesce(max(ie_permite_pass_inf_prim_pass),'S')
into STRICT	ie_permite_passagem_inf_w
from	parametro_atendimento
where	cd_estabelecimento = cd_estabelecimento_p;

if (ie_permite_passagem_inf_w = 'N') and (dt_passagem_p < dt_prim_passagem_w) then
	ie_retorno_w := 'N';
else
	ie_retorno_w := 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
