CREATE OR REPLACE FUNCTION obter_se_permite_prescr (cd_perfil_p bigint, cd_setor_Prescr_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_setor_w	bigint;
ie_permite_w	varchar(10);

c01 CURSOR FOR 
SELECT	coalesce(ie_permite,'S') 
from	regra_prescr_setor 
where	coalesce(cd_perfil,cd_perfil_p) 	= cd_perfil_p 
and		coalesce(cd_setor_atendimento,CASE WHEN coalesce(ie_tipo_setor,'P')='P' THEN cd_setor_Prescr_p  ELSE cd_setor_w END )	= CASE WHEN coalesce(ie_tipo_setor,'P')='P' THEN cd_setor_Prescr_p  ELSE cd_setor_w END  
order by coalesce(cd_perfil,0), 
		 coalesce(cd_setor_atendimento,0);


BEGIN 
	 
select	max(Obter_Setor_Atendimento(nr_atendimento_p)) 
into STRICT	cd_setor_w
;
 
ie_permite_w	:= 'S';
open C01;
loop 
fetch C01 into	 
	ie_permite_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ie_permite_w	:= ie_permite_w;
	end;
end loop;
close C01;
 
return	coalesce(ie_permite_w,'S');
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
