CREATE OR REPLACE FUNCTION obter_se_permite_regra_envio (nr_sequencia_autor_p bigint, nr_seq_regra_envio_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cont_w			bigint := 0;
ie_permite_w		varchar(15) := 'S';
ie_tipo_autorizacao_w	varchar(5);
nr_seq_estagio_w	bigint;
cd_convenio_w		bigint;
ie_interno_w		varchar(2);
dt_autorizacao_w	timestamp;
qt_dias_estagio_w	bigint;
ds_retorno_w		varchar(255);

/*
ie_opcao_p
 1 = permissÃ£o
 2 = qt dias da regra




*/
C01 CURSOR FOR
	SELECT	'S',
		coalesce(qt_dias_estagio,0)
	from	regra_envio_autor_filtro
	where	nr_seq_regra 					= nr_seq_regra_envio_p
	and	coalesce(cd_convenio,cd_convenio_w)  		= cd_convenio_w
	and	coalesce(nr_seq_estagio,nr_seq_estagio_w) 		= nr_seq_estagio_w
	and	coalesce(ie_tipo_autorizacao,ie_tipo_autorizacao_w)  = ie_tipo_autorizacao_w
	and	((coalesce(qt_dias_estagio,0) = 0) or
		((obter_dias_entre_datas(dt_autorizacao_w,clock_timestamp()) >= coalesce(qt_dias_estagio,0)) and (ie_interno_w = '1')))
	order by cd_convenio,
		ie_tipo_autorizacao,
		nr_seq_estagio;


BEGIN

select	count(*)
into STRICT	cont_w
from	regra_envio_autor_filtro
where	nr_seq_regra = nr_seq_regra_envio_p;

if (cont_w > 0) then
	begin
	ie_permite_w := 'N';
	select	coalesce(a.ie_tipo_autorizacao,'0'),
		coalesce(a.nr_seq_estagio,0),
		coalesce(a.cd_convenio,0),
		a.dt_autorizacao,
		b.ie_interno
	into STRICT	ie_tipo_autorizacao_w,
		nr_seq_estagio_w,
		cd_convenio_w,
		dt_autorizacao_w,
		ie_interno_w
	from	autorizacao_convenio a,
		estagio_autorizacao b
	where	a.nr_sequencia = nr_sequencia_autor_p
	and	a.nr_seq_estagio = b.nr_sequencia;



	open C01;
	loop
	fetch C01 into
		ie_permite_w,
		qt_dias_estagio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ie_opcao_p = 1) then
			ds_retorno_w := ie_permite_w;
		elsif (ie_opcao_p = 2) then
			ds_retorno_w := qt_dias_estagio_w;
		end if;
		end;
	end loop;
	close C01;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
