CREATE OR REPLACE FUNCTION obter_se_permite_result_exame (nr_seq_exame_p bigint, nr_prescricao_p bigint, nr_seq_prescr_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_restricao_w	varchar(1);


BEGIN

select ie_restricao
into STRICT ie_restricao_w
from exame_laboratorio
where nr_seq_exame = nr_seq_exame_p;

if (ie_restricao_w = 'S') then

	select	ie_restringe_resultado
	into STRICT	ie_Restricao_w
	from	exame_lab_result_item a,
		exame_lab_resultado b
	where	a.nr_seq_resultado = b.nr_seq_resultado
	and	a.nr_seq_exame	   = nr_seq_exame_p
	and	a.nr_seq_prescr	   = nr_seq_prescr_p
	and	b.nr_prescricao	   = nr_prescricao_p;


	if (ie_Restricao_w = 'S') then

		select CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT ie_Restricao_w
		from exame_lab_liberacao
		where nr_seq_exame = nr_seq_exame_p
		  and (cd_perfil	 = cd_perfil_p or upper(nm_usuario_lib)	 = upper(nm_usuario_p));
	end if;
end if;

select CASE WHEN ie_restricao_w='S' THEN  'N'  ELSE 'S' END
into STRICT ie_restricao_w
;

return ie_restricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
