CREATE OR REPLACE FUNCTION obter_se_permite_vacina ( dt_vacina_p timestamp, nr_seq_vacina_p bigint, cd_pessoa_fisica_p text, nr_atendimento_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resp_w	varchar(1)	:= 'S';


BEGIN

	begin

	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ds_resp_w
	from	paciente_vacina a
	where	trunc(a.dt_vacina)  	 = trunc(dt_vacina_p)
	and	a.nr_seq_vacina  = nr_seq_vacina_p
	and	((a.cd_pessoa_fisica = cd_pessoa_fisica_p) or (a.nr_atendimento = nr_atendimento_p))
	and	a.nr_sequencia <> nr_sequencia_p
	and	exists ( 	SELECT	1
			from	paciente_vacina b
			where	trunc(b.dt_vacina)  	 = trunc(dt_vacina_p)
			and	b.nr_seq_vacina  = nr_seq_vacina_p
			and	a.nr_sequencia <> nr_sequencia_p
			and	((a.cd_pessoa_fisica = cd_pessoa_fisica_p) or (a.nr_atendimento = nr_atendimento_p))
			);
	exception
	when others then
		ds_resp_w := 'S';
	end;

return	ds_resp_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
