CREATE OR REPLACE FUNCTION obter_se_permite_ver_result (nr_seq_exame_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_restricao_w	varchar(1);


BEGIN

select max(ie_restricao)
into STRICT ie_restricao_w
from exame_laboratorio
where nr_seq_exame = nr_seq_exame_p;

if (ie_restricao_w = 'S') then
	select CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT ie_Restricao_w
	from exame_lab_liberacao
	where nr_seq_exame = nr_seq_exame_p
	  and (cd_perfil	 = cd_perfil_p or upper(nm_usuario_lib)	 = upper(nm_usuario_p));
end if;

select CASE WHEN ie_restricao_w='S' THEN  'N'  ELSE 'S' END
into STRICT ie_restricao_w
;

return ie_restricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
