CREATE OR REPLACE FUNCTION obter_se_perm_agecirur (cd_pessoa_agenda_p text, cd_pessoa_usuario_p text, cd_perfil_p bigint, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

		
ie_agenda_w		varchar(01)	:= 'T';
ie_medico_Agenda_w	varchar(1)	:= 'N';
qt_itens_lib_w		integer := 0;

C01 CURSOR FOR
	SELECT	coalesce(a.ie_agenda, 'N')
	from	agenda b, med_permissao a
	where	a.cd_agenda					= b.cd_agenda
	and (a.cd_medico_prop				= cd_pessoa_agenda_p or coalesce(a.cd_medico_prop::text, '') = '')
	and	coalesce(a.cd_pessoa_fisica, cd_pessoa_usuario_p)	= cd_pessoa_usuario_p
	and	((coalesce(nr_seq_grupo_usuario::text, '') = '') 		or (obter_se_usuario_grupo(a.nr_seq_grupo_usuario,wheb_usuario_pck.get_nm_usuario) = 'S'))
	and	coalesce(a.cd_perfil, wheb_usuario_pck.get_cd_perfil)			= wheb_usuario_pck.get_cd_perfil
	and	b.cd_agenda					= cd_agenda_p
	and	b.ie_situacao					= 'A'
	and	b.cd_tipo_Agenda				= 1
	order by coalesce(a.cd_pessoa_fisica,'AAAAAAAAA'), coalesce(a.nr_seq_grupo_usuario,0), coalesce(a.cd_perfil,0), coalesce(a.cd_setor_atendimento,0);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_medico_agenda_w
from	agenda
where	cd_agenda		= cd_agenda_p
and	cd_pessoa_fisica	= cd_pessoa_usuario_p;

if (ie_medico_agenda_w = 'N') then
   select 	count(*)
   into STRICT 	   qt_itens_lib_w
   from	   med_permissao
   where	   cd_agenda	= cd_agenda_p;
end if;

if	(ie_medico_Agenda_w	= 'N' AND qt_itens_lib_w > 0) then
	open C01;
	loop
	fetch C01 into	
		ie_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_agenda_w := ie_agenda_w;
		end;
	end loop;
	close C01;
	
	if (coalesce(ie_agenda_w::text, '') = '') then
		ie_agenda_w := 'N';
	end if;	
else
	ie_agenda_w	:= 'T';
end if;

return	ie_agenda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
