SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_perm_agecons_agenda (cd_pessoa_agenda_p text, cd_pessoa_usuario_p text, cd_perfil_p bigint, cd_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

		
ie_agenda_w	varchar(01)	:= 'T';
ie_modelo_agenda_w	varchar(1);
dia_da_Semana_w		smallint;	
nr_seq_permissao_w	bigint;
cd_setor_atendimento_w	integer;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	agenda b, med_permissao a
	where	a.cd_agenda					= b.cd_agenda
	and	coalesce(a.cd_medico_prop, coalesce(cd_pessoa_agenda_p, '0'))	= coalesce(cd_pessoa_agenda_p, '0')
	and	coalesce(a.cd_pessoa_fisica, cd_pessoa_usuario_p)	= cd_pessoa_usuario_p
	and	coalesce(a.cd_perfil, cd_perfil_p)			= cd_perfil_p
	and		coalesce(a.cd_setor_atendimento, cd_setor_atendimento_w)			= cd_setor_atendimento_w
	and	((coalesce(ie_dia_semana,dia_da_Semana_w) = dia_da_Semana_w) or ((coalesce(ie_dia_semana,dia_da_Semana_w) = 9) and (dia_da_Semana_w not in (7,1))))
	and	b.ie_situacao					= 'A'
	--and	a.cd_setor_atendimento is null
	and	a.cd_agenda					= cd_agenda_p
	order by coalesce(a.cd_pessoa_fisica,'0'),
		 coalesce(a.cd_perfil,0),
		coalesce(a.cd_medico_prop, 0);

C02 CURSOR FOR
	SELECT	a.nr_sequencia
	from	agenda b, med_permissao a
	where	a.cd_agenda					= b.cd_agenda
	and	coalesce(a.cd_pessoa_fisica, cd_pessoa_usuario_p)	= cd_pessoa_usuario_p
	and	coalesce(a.cd_perfil, cd_perfil_p)			= cd_perfil_p
	and		coalesce(a.cd_setor_atendimento, cd_setor_atendimento_w)			= cd_setor_atendimento_w
	and	((coalesce(ie_dia_semana,dia_da_Semana_w) = dia_da_Semana_w) or ((coalesce(ie_dia_semana,dia_da_Semana_w) = 9) and (dia_da_Semana_w not in (7,1))))
	and	b.ie_situacao					= 'A'
	--and	a.cd_setor_atendimento is null
	and	a.cd_agenda					= cd_agenda_p
	order by coalesce(a.cd_pessoa_fisica,'0'),
		 coalesce(a.cd_perfil,0);
		

BEGIN

cd_setor_atendimento_w := coalesce(obter_setor_usuario(nm_usuario_p),0);
ie_modelo_agenda_w := obter_param_usuario(821, 26, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_modelo_agenda_w);

dia_da_Semana_w	:= Obter_Cod_Dia_Semana(dt_agenda_p);

if (ie_modelo_agenda_w = '3')  then
	open C01;
	loop
	fetch C01 into	
		nr_seq_permissao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_seq_permissao_w := nr_seq_permissao_w;
		end;
	end loop;
	close C01;
	
	select	coalesce(min(a.ie_agenda), 'T')
	into STRICT	ie_agenda_w
	from	med_permissao a
	where	a.nr_sequencia = nr_seq_permissao_w;
	
elsif (ie_modelo_agenda_w = '4') then
	
	open C02;
	loop
	fetch C02 into	
		nr_seq_permissao_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		nr_seq_permissao_w := nr_seq_permissao_w;
		end;
	end loop;
	close C02;
	select	coalesce(min(a.ie_agenda), 'T')
	into STRICT	ie_agenda_w
	from	med_permissao a
	where	a.nr_sequencia = nr_seq_permissao_w;
end if;

return	ie_agenda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
