CREATE OR REPLACE FUNCTION obter_se_perm_agecons (cd_pessoa_agenda_p text, cd_pessoa_usuario_p text, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE

		 
ie_agenda_w	varchar(01)	:= 'T';


BEGIN 
 
select	coalesce(min(a.ie_agenda), 'T') 
into STRICT	ie_agenda_w 
from	agenda b, med_permissao a 
where	a.cd_agenda					= b.cd_agenda 
and	a.cd_medico_prop				= cd_pessoa_agenda_p 
and	coalesce(a.cd_pessoa_fisica, cd_pessoa_usuario_p)	= cd_pessoa_usuario_p 
and	coalesce(a.cd_perfil, cd_perfil_p)			= cd_perfil_p 
and	b.ie_situacao					= 'A';
 
return	ie_agenda_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
