CREATE OR REPLACE FUNCTION obter_se_perm_ageserv_espera (cd_agenda_p bigint, cd_pessoa_usuario_p text, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE

		 
ie_agenda_w			varchar(01)	:= 'T';
nr_seq_permissao_w	bigint;
ie_existe_regra_w	varchar(1);
cd_setor_atend_w	setor_atendimento.cd_setor_atendimento%type;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	agenda b, 
			med_permissao a 
	where	a.cd_agenda										= b.cd_agenda 
	and		coalesce(a.cd_pessoa_fisica, cd_pessoa_usuario_p)	= cd_pessoa_usuario_p 
	and		coalesce(a.cd_perfil, cd_perfil_p)					= cd_perfil_p 
	and		coalesce(a.cd_setor_atendimento, cd_setor_atend_w)	= cd_setor_atend_w 
	and		b.cd_agenda				= cd_agenda_p 
	and		b.ie_situacao			= 'A' 
	and		b.cd_tipo_agenda		= 5	 
	order by	coalesce(a.cd_pessoa_fisica,'0'), 
				coalesce(a.cd_perfil,0);


BEGIN 
cd_setor_atend_w	:= wheb_usuario_pck.get_cd_setor_atendimento;
 
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') then	 
	 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_existe_regra_w 
	from	med_permissao 
	where	cd_agenda = cd_agenda_p;
	 
	if (ie_existe_regra_w = 'S') then 
		begin 
		open C01;
		loop 
		fetch C01 into	 
			nr_seq_permissao_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin 
			nr_seq_permissao_w := nr_seq_permissao_w;
			end;
		end loop;
		close C01;
			 
		select	coalesce(max(ie_agenda),'N') 
		into STRICT	ie_agenda_w 
		from	med_permissao 
		where	nr_sequencia = nr_seq_permissao_w;
		end;
	else 
		ie_agenda_w	:= 'T';
	end if;
end if;
 
return	ie_agenda_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
