CREATE OR REPLACE FUNCTION obter_se_perm_classif_perfil (cd_perfil_p bigint, ie_classif_agenda_p text) RETURNS varchar AS $body$
DECLARE


ie_liberado_w		varchar(01) := 'S';
qt_regra_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	agenda_regra_classif_bloq
where	cd_perfil 			= cd_perfil_p
and		ie_classif_agenda	= ie_classif_agenda_p;

if (qt_regra_w > 0) then
	ie_liberado_w	:= 'N';
else
	ie_liberado_w	:= 'S';
end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
